import React from 'react';
import { Link } from 'react-router-dom';

const drugInfo = [
  {
    title: "Analgesics",
    description: "Pain relievers such as ibuprofen and acetaminophen have been used for centuries to alleviate pain and discomfort.",
    imgSrc: "path/to/analgesics.jpg", // Update with actual image path
  },
  {
    title: "Antibiotics",
    description: "Introduced in the 20th century, antibiotics like penicillin revolutionized medicine by effectively treating bacterial infections.",
    imgSrc: "path/to/antibiotics.jpg", // Update with actual image path
  },
  {
    title: "Antivirals",
    description: "Antivirals emerged in the late 20th century to combat viral infections, starting with drugs like oseltamivir for influenza.",
    imgSrc: "path/to/antivirals.jpg", // Update with actual image path
  },
  {
    title: "Antidepressants",
    description: "First developed in the mid-20th century, antidepressants like fluoxetine have helped millions manage depression and anxiety.",
    imgSrc: "path/to/antidepressants.jpg", // Update with actual image path
  },
  {
    title: "Antihypertensives",
    description: "These medications, including lisinopril, have played a critical role in managing high blood pressure since their introduction.",
    imgSrc: "path/to/antihypertensives.jpg", // Update with actual image path
  },
  {
    title: "Antidiabetics",
    description: "Drugs like metformin have transformed diabetes management since their discovery, allowing for better blood sugar control.",
    imgSrc: "path/to/antidiabetics.jpg", // Update with actual image path
  },
  {
    title: "Antihistamines",
    description: "First used in the 1940s, antihistamines like loratadine help relieve allergy symptoms effectively.",
    imgSrc: "path/to/antihistamines.jpg", // Update with actual image path
  },
  {
    title: "Vaccines",
    description: "Vaccines have been pivotal in preventing infectious diseases, with a history that dates back to the late 18th century.",
    imgSrc: "path/to/vaccines.jpg", // Update with actual image path
  },
];

const Home = () => {
  const buttonStyle = {
    margin: '10px',
    padding: '10px 20px',
    backgroundColor: 'green',
    color: 'white',
    textDecoration: 'none',
    borderRadius: '5px',
  };

  const containerStyle = {
    textAlign: 'center',
    marginTop: '50px',
    padding: '20px',
  };

  const drugCardStyle = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    margin: '20px',
    padding: '10px',
    display: 'inline-block',
    width: '250px',
  };

  return (
    <div style={containerStyle}>
      <h1>Welcome to Medical Reminder App</h1>
      <p>Track and manage your medical prescriptions and appointments easily.</p>
      <Link to="/add-reminder" style={buttonStyle}>Add a New Reminder</Link>
      <Link to="/reminders" style={buttonStyle}>View All Reminders</Link>
      <Link to="/login" style={buttonStyle}>Login</Link>

      <h2 style={{ marginTop: '40px' }}>Drug Categories</h2>
      <div>
        {drugInfo.map((drug, index) => (
          <div key={index} style={drugCardStyle}>
            <h3>{drug.title}</h3>
            <img src={drug.imgSrc} alt={drug.title} style={{ width: '100%', borderRadius: '5px' }} />
            <p>{drug.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
