// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  charities: [],
  donors: [],
  totalDonations: 0,
  beneficiaries: [],
  inventory: [],
  stories: []
};

const charitySlice = createSlice({
  name: 'charity',
  initialState,
  reducers: {
    setCharityDetails: (state, action) => {
      // Add the new charity to the charities array
      state.charities.push(action.payload);
    },
    setDonors: (state, action) => {
      state.donors = action.payload;
    },
    setTotalDonations: (state, action) => {
      state.totalDonations = action.payload;
    },
    setInventory: (state, action) => {
      state.inventory = action.payload;
    },
    addBeneficiary: (state, action) => {
      state.beneficiaries.push(action.payload);
    },
    addStory: (state, action) => {
      state.stories.push(action.payload);
    },
    setSelectedCampaign: (state, action) => {
      state.selectedCampaign = action.payload;
    }
  }
});

export const { 
  setCharityDetails, 
  setDonors, 
  setTotalDonations, 
  setInventory, 
  addBeneficiary, 
  addStory,
  setSelectedCampaign 
} = charitySlice.actions;

export default charitySlice.reducer;
