import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { donors, totalDonations, charities } = useSelector(state => state.charity);
  const donationHistory = useSelector(state => state.donation.donationHistory);

  useEffect(() => {
    // Update total donations from the donation history
    const total = donationHistory.reduce((sum, donation) => sum + parseFloat(donation.amount), 0);
    dispatch(setTotalDonations(total));
  }, [donationHistory, dispatch]);

  return (
    <div>
      <h2>Charity Dashboard</h2>

      <h3>Total Donations: ${totalDonations}</h3>

      <h3>Donors</h3>
      <ul>
        {donors.map((donor, index) => (
          <li key={index}>
            {donor.name} donated ${donor.amount}
          </li>
        ))}
      </ul>

      {/* Display Donations History */}
      <h3>Donation History</h3>
      <ul>
        {donationHistory.map((donation, index) => (
          <li key={index}>
            <h4>{donation.campaignTitle}</h4>
            <p>Amount: ${donation.amount}</p>
            <p>Date: {new Date(donation.date).toLocaleDateString()}</p>
            <p>{donation.isAnonymous ? 'Anonymous Donation' : `Donor: ${donation.userName}`}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityDashboard;
