import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory, setCharityDetails } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, user } = useSelector(state => state.charity);  // Added user state
  const [newStoryTitle, setNewStoryTitle] = useState('');
  const [newStoryContent, setNewStoryContent] = useState('');
  const [selectedCharityId, setSelectedCharityId] = useState('');
  const [newBeneficiaryName, setNewBeneficiaryName] = useState('');
  const [newBeneficiaryAge, setNewBeneficiaryAge] = useState('');

  useEffect(() => {
    // Simulate fetching data from API
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
    dispatch(setTotalDonations(300));
    dispatch(setInventory([{ id: 1, item: 'Food Packages', quantity: 50 }]));
  }, [dispatch]);

  // Admin-specific actions
  const handleApproveCharity = (charityId) => {
    const updatedCharities = charities.map(charity =>
      charity.id === charityId ? { ...charity, status: 'approved' } : charity
    );
    dispatch(setCharityDetails(updatedCharities)); // Update the charity list in Redux
    alert('Charity approved!');
  };

  const handleRejectCharity = (charityId) => {
    const updatedCharities = charities.filter(charity => charity.id !== charityId);
    dispatch(setCharityDetails(updatedCharities)); // Remove the rejected charity
    alert('Charity rejected!');
  };

  const handleDeleteCharity = (charityId) => {
    const updatedCharities = charities.filter(charity => charity.id !== charityId);
    dispatch(setCharityDetails(updatedCharities)); // Remove the deleted charity
    alert('Charity deleted!');
  };

  return (
    <div>
      <h2>Charity Dashboard</h2>

      {/* Display Charity Information */}
      <h3>Charities</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map(charity => (
            <li key={charity.id}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Amount to raise: ${charity.amountToRaise}</p>
              <p>Status: {charity.status || 'Pending'}</p>
              {user.isAdmin && (  // Only show admin options if the user is an admin
                <div>
                  <button onClick={() => handleApproveCharity(charity.id)}>Approve</button>
                  <button onClick={() => handleRejectCharity(charity.id)}>Reject</button>
                  <button onClick={() => handleDeleteCharity(charity.id)}>Delete</button>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No charities available.</p>
      )}

      {/* Add Story & Beneficiary Form */}
      <div>
        {/* Add Beneficiary Form */}
        <h3>Add a Beneficiary</h3>
        <div>
          <label>
            Select Charity:
            <select onChange={(e) => setSelectedCharityId(e.target.value)} value={selectedCharityId}>
              <option value="">-- Select a charity --</option>
              {charities.map(charity => (
                <option key={charity.id} value={charity.id}>{charity.name}</option>
              ))}
            </select>
          </label>

          <input
            type="text"
            placeholder="Beneficiary Name"
            value={newBeneficiaryName}
            onChange={(e) => setNewBeneficiaryName(e.target.value)}
          />
          <input
            type="number"
            placeholder="Beneficiary Age"
            value={newBeneficiaryAge}
            onChange={(e) => setNewBeneficiaryAge(e.target.value)}
          />
          <button>Submit Beneficiary</button>
        </div>

        {/* Add Story Form */}
        <h3>Add a Story</h3>
        <div>
          <label>
            Select Charity:
            <select onChange={(e) => setSelectedCharityId(e.target.value)} value={selectedCharityId}>
              <option value="">-- Select a charity --</option>
              {charities.map(charity => (
                <option key={charity.id} value={charity.id}>{charity.name}</option>
              ))}
            </select>
          </label>

          <input
            type="text"
            placeholder="Story Title"
            value={newStoryTitle}
            onChange={(e) => setNewStoryTitle(e.target.value)}
          />
          <textarea
            placeholder="Story Content"
            value={newStoryContent}
            onChange={(e) => setNewStoryContent(e.target.value)}
          />
          <button>Post Story</button>
        </div>
      </div>
    </div>
  );
};

export default CharityDashboard;
