import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { Provider } from 'react-redux';
import store from './redux/store';
import Navbar from './components/Navbar';
import Home from './components/Home';
import DonationList from './components/DonationList';
import DonateForm from './components/DonateForm';
import DonationHistory from './components/DonationHistory';
import CharityApplication from './components/CharityApplication';
import CharityDashboard from './components/CharityDashboard';
import AdminDashboard from './components/AdminDashboard'; // import AdminDashboard
import './App.css';

function App() {
  const { currentUser } = useSelector(state => state.charity);

  return (
    <Provider store={store}>
      <Router>
        <div className="App">
          <Navbar />
          <Routes>
            {/* Home Page */}
            <Route path="/" element={<Home />} />

            {/* Donation User Pages */}
            <Route path="/donations" element={<DonationList />} />
            <Route path="/donate" element={<DonateForm />} />
            <Route path="/donation-history" element={<DonationHistory />} />

            {/* Charity User Pages */}
            <Route path="/charity-application" element={<CharityApplication />} />
            <Route path="/charity-dashboard" element={<CharityDashboard />} />

            {/* Admin Dashboard (Protected Route) */}
            <Route 
              path="/admin-dashboard" 
              element={currentUser && currentUser.role === 'admin' ? <AdminDashboard /> : <p>You are not authorized to view this page.</p>} 
            />
          </Routes>
        </div>
      </Router>
    </Provider>
  );
}

export default App;
