import React from 'react';
import { Link } from 'react-router-dom';

const drugCategories = {
  ByPurpose: [
    {
      title: "Analgesics",
      description: "Pain relievers such as ibuprofen and acetaminophen have been used for centuries to alleviate pain and discomfort. Their development has significantly improved the management of acute and chronic pain.",
      imgSrc: "path/to/analgesics.jpg", // Update with actual image path
    },
    {
      title: "Antibiotics",
      description: "Introduced in the 20th century, antibiotics like penicillin revolutionized medicine by effectively treating bacterial infections, saving countless lives.",
      imgSrc: "path/to/antibiotics.jpg", // Update with actual image path
    },
    {
      title: "Antivirals",
      description: "Antivirals emerged in the late 20th century to combat viral infections, with oseltamivir being a key drug for influenza treatment, showcasing the evolution of antiviral therapies.",
      imgSrc: "path/to/antivirals.jpg", // Update with actual image path
    },
    {
      title: "Antidepressants",
      description: "First developed in the mid-20th century, antidepressants like fluoxetine have helped millions manage depression and anxiety, representing a crucial advancement in mental health treatment.",
      imgSrc: "path/to/antidepressants.jpg", // Update with actual image path
    },
    {
      title: "Antihypertensives",
      description: "These medications, including lisinopril, have played a critical role in managing high blood pressure since their introduction, significantly reducing cardiovascular disease risks.",
      imgSrc: "path/to/antihypertensives.jpg", // Update with actual image path
    },
    {
      title: "Antidiabetics",
      description: "Drugs like metformin have transformed diabetes management since their discovery, allowing for better blood sugar control and improving patients' quality of life.",
      imgSrc: "path/to/antidiabetics.jpg", // Update with actual image path
    },
    {
      title: "Antihistamines",
      description: "First used in the 1940s, antihistamines like loratadine help relieve allergy symptoms effectively, representing a significant advancement in allergy treatment.",
      imgSrc: "path/to/antihistamines.jpg", // Update with actual image path
    },
    {
      title: "Vaccines",
      description: "Vaccines have been pivotal in preventing infectious diseases, with a history that dates back to the late 18th century, drastically reducing morbidity and mortality.",
      imgSrc: "path/to/vaccines.jpg", // Update with actual image path
    },
  ],
  ByForm: [
    {
      title: "Tablets/Capsules",
      description: "Solid forms taken orally.",
    },
    {
      title: "Liquids/Syrups",
      description: "Measured doses in liquid form.",
    },
    {
      title: "Injections",
      description: "Delivered via needle (e.g., intramuscular, subcutaneous).",
    },
    {
      title: "Topicals",
      description: "Applied directly to the skin (e.g., creams, ointments).",
    },
    {
      title: "Inhalers",
      description: "For respiratory issues (e.g., asthma medications).",
    },
    {
      title: "Transdermal Patches",
      description: "Medications delivered through the skin.",
    },
  ],
  ByMethod: [
    {
      title: "Oral",
      description: "Taken by mouth.",
    },
    {
      title: "Sublingual/Buccal",
      description: "Dissolved under the tongue or in the cheek.",
    },
    {
      title: "Intravenous (IV)",
      description: "Directly into the bloodstream.",
    },
    {
      title: "Intramuscular (IM)",
      description: "Into a muscle.",
    },
    {
      title: "Subcutaneous",
      description: "Under the skin.",
    },
  ],
  AlternativeAndComplementary: [
    {
      title: "Herbal Medicine",
      description: "Dating back thousands of years, herbal medicine utilizes plant extracts to treat various ailments, forming the basis of many traditional healing practices worldwide.",
      imgSrc: "path/to/herbal_medicine.jpg", // Update with actual image path
    },
    {
      title: "Homeopathy",
      description: "Founded in the late 18th century by Samuel Hahnemann, homeopathy is based on the principle of 'like cures like,' using highly diluted substances to stimulate the body's healing.",
      imgSrc: "path/to/homeopathy.jpg", // Update with actual image path
    },
    {
      title: "Acupuncture",
      description: "An ancient practice from traditional Chinese medicine, acupuncture involves inserting needles at specific points to restore balance and promote healing, with origins dating back over 2,000 years.",
      imgSrc: "path/to/acupuncture.jpg", // Update with actual image path
    },
    {
      title: "Chiropractic",
      description: "Emerging in the late 19th century, chiropractic focuses on diagnosing and treating musculoskeletal disorders, primarily through manual adjustment and manipulation of the spine.",
      imgSrc: "path/to/chiropractic.jpg", // Update with actual image path
    },
  ],
};

const Home = () => {
  const buttonStyle = {
    margin: '10px',
    padding: '10px 20px',
    backgroundColor: 'green',
    color: 'white',
    textDecoration: 'none',
    borderRadius: '5px',
  };

  const containerStyle = {
    textAlign: 'center',
    marginTop: '50px',
    padding: '20px',
  };

  const categoryStyle = {
    marginTop: '40px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
  };

  const drugCardStyle = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    margin: '20px',
    padding: '10px',
    display: 'inline-block',
    width: '250px',
  };

  return (
    <div style={containerStyle}>
      <h1>Welcome to Medical Reminder App</h1>
      <p>Track and manage your medical prescriptions and appointments easily.</p>
      <Link to="/add-reminder" style={buttonStyle}>Add a New Reminder</Link>
      <Link to="/reminders" style={buttonStyle}>View All Reminders</Link>
      <Link to="/login" style={buttonStyle}>Login</Link>

      {Object.entries(drugCategories).map(([category, drugs]) => (
        <div key={category} style={categoryStyle}>
          <h2>{category.replace(/([A-Z])/g, ' $1').trim()}</h2>
          <div>
            {drugs.map((drug, index) => (
              <div key={index} style={drugCardStyle}>
                <h3>{drug.title}</h3>
                {drug.imgSrc && <img src={drug.imgSrc} alt={drug.title} style={{ width: '100%', borderRadius: '5px' }} />}
                <p>{drug.description}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Home;
