import React from "react";
import { Link } from "react-router-dom";

const Login = () => {
  const styles = {
    addUser: {
      backgroundColor: "white",
      width: "30%", // Slightly wider for better spacing
      margin: "50px auto",
      padding: "40px",
      borderRadius: "10px",
      boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)", // Softer shadow
      transition: "box-shadow 0.3s", // Smooth transition for shadow
    },
    addUserForm: {
      width: "100%",
      display: "flex",
      flexDirection: "column",
    },
    label: {
      marginTop: "15px", // Increased spacing
      fontWeight: "bold", // Bold labels
      color: "#333", // Darker color for better readability
    },
    input: {
      marginTop: "5px",
      padding: "10px",
      borderRadius: "5px",
      border: "1px solid #ccc", // Light border
      transition: "border 0.3s", // Smooth transition for border
    },
    button: {
      marginTop: "15px",
      backgroundColor: "darkcyan",
      color: "white",
      border: "none",
      padding: "10px",
      cursor: "pointer",
      borderRadius: "5px",
      fontSize: "16px",
      fontWeight: "bold",
      transition: "background-color 0.3s", // Smooth transition for button
    },
    buttonHover: {
      backgroundColor: "teal", // Darker shade on hover
    },
    login: {
      marginTop: "30px",
      textAlign: "center",
    },
    linkButton: {
      width: "100%", // Full width for link button
      backgroundColor: "lightblue",
      color: "white",
      border: "none",
      padding: "10px",
      cursor: "pointer",
      borderRadius: "5px",
      textDecoration: "none",
      textAlign: "center",
      display: "inline-block", // Inline block for better styling
      transition: "background-color 0.3s", // Smooth transition for link
    },
    linkHover: {
      backgroundColor: "deepskyblue", // Darker shade on hover
    },
    title: {
      textAlign: "center",
      fontWeight: "bold",
      color: "darkcyan",
      textTransform: "uppercase",
      textShadow: "1px 1px 2px rgba(0, 0, 0, 0.7)",
    },
  };

  return (
    <div style={styles.addUser}>
      <h3 style={styles.title}>Sign in</h3>
      <form style={styles.addUserForm}>
        <div>
          <label htmlFor="email" style={styles.label}>Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            autoComplete="off"
            placeholder="Enter your Email"
            style={styles.input}
            onFocus={(e) => e.target.style.border = "1px solid darkcyan"} // Change border on focus
            onBlur={(e) => e.target.style.border = "1px solid #ccc"} // Reset border on blur
          />
          <label htmlFor="password" style={styles.label}>Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            autoComplete="off"
            placeholder="Enter your Password"
            style={styles.input}
            onFocus={(e) => e.target.style.border = "1px solid darkcyan"}
            onBlur={(e) => e.target.style.border = "1px solid #ccc"}
          />
          <button 
            type="submit" 
            style={styles.button}
            onMouseOver={(e) => e.currentTarget.style.backgroundColor = styles.buttonHover.backgroundColor} // Button hover effect
            onMouseOut={(e) => e.currentTarget.style.backgroundColor = styles.button.backgroundColor} // Reset color on mouse out
          >
            Login
          </button>
        </div>
      </form>
      <div style={styles.login}>
        <p>Don't have an account?</p>
        <Link 
          to="/" 
          style={styles.linkButton}
          onMouseOver={(e) => e.currentTarget.style.backgroundColor = styles.linkHover.backgroundColor} // Link hover effect
          onMouseOut={(e) => e.currentTarget.style.backgroundColor = styles.linkButton.backgroundColor} // Reset color on mouse out
        >
          Sign Up
        </Link>
      </div>
    </div>
  );
};

export default Login;
