import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ user }) => {
  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    alignItems: 'center',
    background: 'linear-gradient(90deg, #007bff, #0056b3)', // Gradient background
    padding: '15px 30px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  };

  const linkStyle = {
    color: 'white',
    textDecoration: 'none',
    fontSize: '18px',
    fontWeight: 'bold', // Bold text
    transition: 'color 0.3s, transform 0.3s',
  };

  const handleMouseEnter = (e) => {
    e.target.style.color = '#ffd700'; // Change color on hover
    e.target.style.transform = 'scale(1.1)'; // Slightly enlarge
  };

  const handleMouseLeave = (e) => {
    e.target.style.color = 'white'; // Revert color
    e.target.style.transform = 'scale(1)'; // Revert size
  };

  return (
    <nav style={navbarStyle}>
      <Link to="/" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Home</Link>
      <Link to="/reminders" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Medication Reminders</Link>
      <Link to="/appointments" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Appointment Reminders</Link>
      <Link to="/calendar" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Calendar</Link>
      {user ? (
        <span style={{ ...linkStyle, fontStyle: 'italic' }}>Welcome, {user.name}</span> // Display user name with italic style
      ) : null}
    </nav>
  );
};

export default Navbar;
