import React from 'react';
import { useDispatch } from 'react-redux';
import { setSelectedCampaign } from '../store/donationSlice';
import { Link } from 'react-router-dom';

const DonationList = () => {
  const dispatch = useDispatch();
  const campaigns = [
    {
      id: 1,
      title: 'Help Build a School',
      description: 'Donate to help build a school in rural areas.',
      amountNeeded: 5000,
      beneficiaryStories: [
        "The foundation for the new school is now complete.",
        "Your donations helped provide textbooks for the students!",
      ]
    },
    {
      id: 2,
      title: 'Feed the Hungry',
      description: 'Donate to provide food for the homeless.',
      amountNeeded: 3000,
      beneficiaryStories: [
        "We've served over 500 meals this week thanks to your generosity!",
        "With your help, we’re able to provide warm food every day.",
      ]
    },
    {
      id: 3,
      title: 'Support Medical Aid',
      description: 'Donate to fund medical supplies and healthcare for underprivileged communities.',
      amountNeeded: 10000,
      beneficiaryStories: [
        "We've successfully distributed medical kits to remote villages.",
        "Your contributions helped save lives by providing urgent medical supplies.",
      ]
    }
  ];

  const handleCampaignClick = (campaign) => {
    dispatch(setSelectedCampaign(campaign));
  };

  return (
    <div>
      <h2>Donation Campaigns</h2>
      <ul>
        {campaigns.map(campaign => (
          <li key={campaign.id}>
            <h3>{campaign.title}</h3>
            <p>{campaign.description}</p>
            <p><strong>Amount Needed:</strong> ${campaign.amountNeeded}</p>
            <Link to="/donate" onClick={() => handleCampaignClick(campaign)}>
              <button>Donate Now</button>
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DonationList;
