// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  charities: [],
  donors: [],
  totalDonations: 0,
  beneficiaries: [],
  inventory: [],
  stories: [],
};

const charitySlice = createSlice({
  name: 'charity',
  initialState,
  reducers: {
    setCharities: (state, action) => {
      state.charities = action.payload;
    },
    setDonors: (state, action) => {
      state.donors = action.payload;
    },
    addDonor: (state, action) => {
      state.donors.push(action.payload);
    },
    setTotalDonations: (state, action) => {
      state.totalDonations += action.payload;
    },
    addStory: (state, action) => {
      state.stories.push(action.payload);
    },
    addBeneficiary: (state, action) => {
      state.beneficiaries.push(action.payload);
    },
  },
});

export const { 
  setCharities, 
  setDonors, 
  addDonor, 
  setTotalDonations, 
  addStory, 
  addBeneficiary 
} = charitySlice.actions;

export default charitySlice.reducer;
