import React from 'react';

const Login = () => {
  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundImage: "url('https://images.unsplash.com/photo-1500993855538-c6a99f437aa7?ixlib=rb-1.2.1&auto=format&fit=crop&w=750&q=80')",
    backgroundSize: 'cover',
  };

  const formContainerStyle = {
    position: 'relative',
    width: '768px',
    maxWidth: '100%',
    minHeight: '480px',
    background: 'rgba(45, 52, 54, 0.8)',
    borderRadius: '10px',
    boxShadow: '0 14px 28px rgba(0, 0, 0, 0.25), 0 10px 10px rgba(0, 0, 0, 0.22)',
    overflow: 'hidden',
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    padding: '0 50px',
    height: '100%',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
  };

  const inputStyle = {
    background: '#eee',
    padding: '12px 15px',
    margin: '8px 0',
    width: '100%',
    border: 'none',
    borderBottom: '1px solid #fff',
    color: '#fff',
  };

  const buttonStyle = {
    borderRadius: '20px',
    border: '1px solid #ff4b2b',
    background: '#ff4b2b',
    color: '#fff',
    fontSize: '12px',
    fontWeight: 'bold',
    padding: '12px 45px',
    letterSpacing: '1px',
    textTransform: 'uppercase',
    transition: 'transform 80ms ease-in',
  };

  const overlayContainerStyle = {
    position: 'absolute',
    top: 0,
    left: '50%',
    width: '50%',
    height: '100%',
    overflow: 'hidden',
    transition: 'transform 0.6s ease-in-out',
    zIndex: 100,
  };

  const overlayStyle = {
    background: 'linear-gradient(to right, #ff4b2b, #ff416c) no-repeat 0 0 / cover',
    color: '#fff',
    position: 'absolute',
    left: '-100%',
    height: '100%',
    width: '200%',
    transition: 'transform 0.6s ease-in-out',
  };

  const overlayPanelStyle = {
    position: 'absolute',
    top: 0,
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '0 40px',
    height: '100%',
    width: '50%',
    textAlign: 'center',
    transition: 'transform 0.6s ease-in-out',
  };

  const signUpButton = React.useRef(null);
  const signInButton = React.useRef(null);
  const container = React.useRef(null);

  React.useEffect(() => {
    const handleSignUpClick = () => {
      container.current.classList.add("right-panel-active");
    };

    const handleSignInClick = () => {
      container.current.classList.remove("right-panel-active");
    };

    signUpButton.current.addEventListener('click', handleSignUpClick);
    signInButton.current.addEventListener('click', handleSignInClick);

    return () => {
      signUpButton.current.removeEventListener('click', handleSignUpClick);
      signInButton.current.removeEventListener('click', handleSignInClick);
    };
  }, []);

  return (
    <div style={containerStyle} ref={container}>
      <div style={formContainerStyle}>
        {/* Sign Up Form */}
        <div className="form-container sign-up-container">
          <form style={formStyle}>
            <h1>Create Account</h1>
            <span>or use your email for registration</span>
            <input type="text" placeholder="Name" required style={inputStyle} />
            <input type="email" placeholder="Email" required style={inputStyle} />
            <input type="password" placeholder="Password" required style={inputStyle} />
            <button type="submit" style={buttonStyle}>Sign Up</button>
          </form>
        </div>

        {/* Sign In Form */}
        <div className="form-container sign-in-container">
          <form style={formStyle}>
            <h1>Sign In</h1>
            <span>or use your account</span>
            <input type="email" placeholder="Email" required style={inputStyle} />
            <input type="password" placeholder="Password" required style={inputStyle} />
            <button type="submit" style={buttonStyle}>Sign In</button>
          </form>
        </div>

        {/* Overlay for transition between sign-in and sign-up */}
        <div style={overlayContainerStyle}>
          <div style={overlayStyle}>
            <div style={overlayPanelStyle}>
              <h1>Welcome Back</h1>
              <p>To keep connected with us please login with your personal info</p>
              <button className="ghost" ref={signInButton} style={buttonStyle}>Sign In</button>
            </div>
            <div style={overlayPanelStyle}>
              <h1>Hello, Friend</h1>
              <p>Enter your personal details and start your journey with us</p>
              <button className="ghost" ref={signUpButton} style={buttonStyle}>Sign Up</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
