import React from "react";
import { Link } from "react-router-dom";

const Login = () => {
  const styles = {
    addUser: {
      backgroundColor: "white",
      width: "25%",
      margin: "50px auto",
      padding: "40px",
      borderRadius: "10px",
      boxShadow: "1px 1px 8px rgba(0, 0, 0, 0.065)",
    },
    inputGroup: {
      width: "100%",
      display: "flex",
      flexDirection: "column",
    },
    label: {
      marginTop: "10px",
    },
    input: {
      marginTop: "5px",
      padding: "10px",
    },
    button: {
      marginTop: "10px",
    },
    login: {
      marginTop: "30px",
      textAlign: "center",
    },
    signUpButton: {
      width: "50%",
    },
    heading: {
      textAlign: "center",
      fontWeight: "bold",
      color: "darkcyan",
      textTransform: "uppercase",
      textShadow: "1px 1px 2px rgba(0, 0, 0, 0.7)",
    },
  };

  return (
    <div style={styles.addUser}>
      <h3 style={styles.heading}>Sign in</h3>
      <form className="addUserForm">
        <div style={styles.inputGroup}>
          <label htmlFor="email" style={styles.label}>Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            autoComplete="off"
            placeholder="Enter your Email"
            style={styles.input}
          />
          <label htmlFor="password" style={styles.label}>Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            autoComplete="off"
            placeholder="Enter your Password"
            style={styles.input}
          />
          <button type="submit" className="btn btn-primary" style={styles.button}>
            Login
          </button>
        </div>
      </form>
      <div style={styles.login}>
        <p>Don't have an account? </p>
        <Link to="/" className="btn btn-success" style={styles.signUpButton}>
          Sign Up
        </Link>
      </div>
    </div>
  );
};

export default Login;
