// CharityDashboard.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, donors, totalDonations, beneficiaries, inventory, stories } = useSelector(state => state.charity);

  useEffect(() => {
    // Simulate fetching data from API
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
    dispatch(setTotalDonations(300));
    dispatch(setInventory([{ id: 1, item: 'Food Packages', quantity: 50 }]));
    dispatch(addBeneficiary({ id: 1, name: 'Ben', age: 12 }));
    dispatch(addStory({ title: 'Ben Gets a Food Package', content: 'Ben was very happy to receive a food package.' }));
  }, [dispatch]);

  const nonAnonymousDonors = donors.filter(donor => !donor.anonymous);
  const anonymousDonorsAmount = donors.filter(donor => donor.anonymous).reduce((total, donor) => total + donor.amount, 0);

  return (
    <div>
      <h2>Charity Dashboard</h2>

      <h3>Charities</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map(charity => (
            <li key={charity.name}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Amount to raise: ${charity.amountToRaise}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No charities available.</p>
      )}

      {/* Other sections */}
      <h3>Donors</h3>
      <h4>Non-Anonymous Donors:</h4>
      <ul>
        {nonAnonymousDonors.map(donor => (
          <li key={donor.id}>{donor.name} donated ${donor.amount}</li>
        ))}
      </ul>
      <h4>Anonymous Donors:</h4>
      <p>Total anonymous donations: ${anonymousDonorsAmount}</p>

      <h3>Total Donations: ${totalDonations}</h3>

      <h3>Beneficiaries</h3>
      <ul>
        {beneficiaries.map(beneficiary => (
          <li key={beneficiary.id}>{beneficiary.name}, Age: {beneficiary.age}</li>
        ))}
      </ul>

      <h3>Inventory</h3>
      <ul>
        {inventory.map(item => (
          <li key={item.id}>{item.item} (Quantity: {item.quantity})</li>
        ))}
      </ul>

      <h3>Stories</h3>
      <ul>
        {stories.map((story, index) => (
          <li key={index}>
            <h4>{story.title}</h4>
            <p>{story.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityDashboard;
