import React, { useState, useEffect, useRef } from 'react';

const Login = ({ onLogin }) => {
  const signUpButton = useRef(null);
  const signInButton = useRef(null);
  const container = useRef(null);

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const currentSignUpButton = signUpButton.current;
    const currentSignInButton = signInButton.current;

    const handleSignUpClick = () => {
      container.current.classList.add("right-panel-active");
    };

    const handleSignInClick = () => {
      container.current.classList.remove("right-panel-active");
    };

    currentSignUpButton.addEventListener('click', handleSignUpClick);
    currentSignInButton.addEventListener('click', handleSignInClick);

    // Cleanup function
    return () => {
      currentSignUpButton.removeEventListener('click', handleSignUpClick);
      currentSignInButton.removeEventListener('click', handleSignInClick);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement login logic here
    // For demonstration, assume login is always successful
    onLogin(); // Call this to manage login state in App.js
  };

  return (
    <div className="container" id="container" ref={container} style={containerStyle}>
      {/* Sign Up Form */}
      <div className="form-container sign-up-container" style={formContainerStyle}>
        <form action="#">
          <h1 style={headingStyle}>Create Account</h1>
          <div className="social-container">
            <a href="#" className="social" style={socialIconStyle}>
              <ion-icon name="logo-facebook"></ion-icon>
            </a>
            <a href="#" className="social" style={socialIconStyle}>
              <ion-icon name="logo-googleplus"></ion-icon>
            </a>
            <a href="#" className="social" style={socialIconStyle}>
              <ion-icon name="logo-linkedin"></ion-icon>
            </a>
          </div>
          <span style={spanStyle}>or use your email for registration</span>
          <input type="text" placeholder="Name" required style={inputStyle} />
          <input type="email" placeholder="Email" required style={inputStyle} />
          <input type="password" placeholder="Password" required style={inputStyle} />
          <button type="submit" style={buttonStyle}>Sign Up</button>
        </form>
      </div>

      {/* Sign In Form */}
      <div className="form-container sign-in-container" style={formContainerStyle}>
        <form onSubmit={handleSubmit}>
          <h1 style={headingStyle}>Sign In</h1>
          <div className="social-container">
            <a href="#" className="social" style={socialIconStyle}>
              <ion-icon name="logo-facebook"></ion-icon>
            </a>
            <a href="#" className="social" style={socialIconStyle}>
              <ion-icon name="logo-googleplus"></ion-icon>
            </a>
            <a href="#" className="social" style={socialIconStyle}>
              <ion-icon name="logo-linkedin"></ion-icon>
            </a>
          </div>
          <span style={spanStyle}>or use your account</span>
          <input
            type="email"
            placeholder="Email"
            required
            style={inputStyle}
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            required
            style={inputStyle}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit" style={buttonStyle}>Sign In</button>
        </form>
      </div>

      {/* Overlay for transition between sign-in and sign-up */}
      <div className="overlay-container" style={overlayContainerStyle}>
        <div className="overlay" style={overlayStyle}>
          <div className="overlay-panel overlay-left" style={overlayPanelStyle}>
            <h1 style={overlayHeadingStyle}>Welcome Back</h1>
            <p style={overlayTextStyle}>To keep connected with us please login with your personal info</p>
            <button className="ghost" id="signIn" ref={signInButton} style={ghostButtonStyle}>Sign In</button>
          </div>
          <div className="overlay-panel overlay-right" style={overlayPanelStyle}>
            <h1 style={overlayHeadingStyle}>Hello, Friend</h1>
            <p style={overlayTextStyle}>Enter your personal details and start your journey with us</p>
            <button className="ghost" id="signUp" ref={signUpButton} style={ghostButtonStyle}>Sign Up</button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Inline styles
const containerStyle = {
  backgroundColor: '#f0f0f0',
  width: '100%',
  height: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  position: 'relative',
};

const formContainerStyle = {
  backgroundColor: '#fff',
  borderRadius: '10px',
  boxShadow: '0 2px 20px rgba(0, 0, 0, 0.1)',
  padding: '40px',
  width: '300px',
  transition: 'transform 0.6s ease-in-out',
  position: 'absolute',
};

const headingStyle = {
  fontSize: '24px',
  marginBottom: '20px',
  textAlign: 'center',
};

const socialIconStyle = {
  margin: '0 10px',
  fontSize: '24px',
  color: '#666',
};

const spanStyle = {
  display: 'block',
  margin: '20px 0',
  textAlign: 'center',
};

const inputStyle = {
  width: '100%',
  padding: '10px',
  margin: '10px 0',
  border: '1px solid #ccc',
  borderRadius: '5px',
};

const buttonStyle = {
  width: '100%',
  padding: '10px',
  border: 'none',
  borderRadius: '5px',
  backgroundColor: '#5cb85c',
  color: '#fff',
  cursor: 'pointer',
  transition: 'background-color 0.3s ease',
};

const overlayContainerStyle = {
  position: 'absolute',
  top: '0',
  left: '0',
  width: '100%',
  height: '100%',
  overflow: 'hidden',
  borderRadius: '10px',
};

const overlayStyle = {
  backgroundColor: '#5cb85c',
  width: '200%',
  height: '100%',
  display: 'flex',
  transition: 'transform 0.6s ease-in-out',
  transform: 'translateX(-100%)',
};

const overlayPanelStyle = {
  width: '50%',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  color: '#fff',
  padding: '20px',
};

const overlayHeadingStyle = {
  fontSize: '24px',
  marginBottom: '20px',
};

const overlayTextStyle = {
  marginBottom: '20px',
  textAlign: 'center',
};

const ghostButtonStyle = {
  backgroundColor: 'transparent',
  border: '1px solid #fff',
  color: '#fff',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
  transition: 'background-color 0.3s ease',
};

export default Login;
