// src/components/Login.js
import React, { useState, useEffect, useRef } from 'react';

const Login = ({ onLogin }) => {
  const signUpButton = useRef(null);
  const signInButton = useRef(null);
  const container = useRef(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    const currentSignUpButton = signUpButton.current;
    const currentSignInButton = signInButton.current;

    const handleSignUpClick = () => {
      container.current.classList.add("right-panel-active");
    };

    const handleSignInClick = () => {
      container.current.classList.remove("right-panel-active");
    };

    currentSignUpButton.addEventListener('click', handleSignUpClick);
    currentSignInButton.addEventListener('click', handleSignInClick);

    return () => {
      currentSignUpButton.removeEventListener('click', handleSignUpClick);
      currentSignInButton.removeEventListener('click', handleSignInClick);
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you can add logic for actual login, such as checking credentials.
    // If successful, call onLogin:
    onLogin();
  };

  return (
    <div className="container" id="container" ref={container} style={containerStyle}>
      {/* Sign In Form */}
      <div className="form-container sign-in-container" style={formContainerStyle}>
        <form onSubmit={handleSubmit}>
          <h1 style={headingStyle}>Sign In</h1>
          <input 
            type="email" 
            placeholder="Email" 
            required 
            style={inputStyle} 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <input 
            type="password" 
            placeholder="Password" 
            required 
            style={inputStyle} 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button type="submit" style={buttonStyle}>Sign In</button>
        </form>
      </div>
      {/* Other elements remain the same... */}
    </div>
  );
};

// ... (rest of the Login component)
