// src/components/DonateForm.js
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonationAmount, setRecurring, setAnonymity } from '../store/donationSlice';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import CheckoutForm from './CheckoutForm'; // Create a checkout form for Stripe

const stripePromise = loadStripe('your-publishable-key-from-stripe');

const DonateForm = () => {
  const dispatch = useDispatch();
  const selectedCampaign = useSelector(state => state.donation.selectedCampaign);
  const [amount, setAmount] = useState('');
  const [recurring, setRecurring] = useState(false);
  const [anonymity, setAnonymity] = useState(false);

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
    dispatch(setDonationAmount(e.target.value));
  };

  const handleRecurringChange = (e) => {
    setRecurring(e.target.checked);
    dispatch(setRecurring(e.target.checked));
  };

  const handleAnonymityChange = (e) => {
    setAnonymity(e.target.checked);
    dispatch(setAnonymity(e.target.checked));
  };

  return (
    <div>
      <h2>Donate to {selectedCampaign.title}</h2>
      <form>
        <label>
          Donation Amount ($):
          <input type="number" value={amount} onChange={handleAmountChange} required />
        </label>
        <br />
        <label>
          Recurring Donation:
          <input type="checkbox" checked={recurring} onChange={handleRecurringChange} />
        </label>
        <br />
        <label>
          Anonymity:
          <input type="checkbox" checked={anonymity} onChange={handleAnonymityChange} />
        </label>
        <br />
        <Elements stripe={stripePromise}>
          <CheckoutForm donationAmount={amount} />
        </Elements>
      </form>
    </div>
  );
};

export default DonateForm;
