// CharityApplication.js
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setCharityDetails } from '../redux/charitySlice';

const CharityApplication = () => {
  const [charityName, setCharityName] = useState('');
  const [charityDescription, setCharityDescription] = useState('');
  const [amountToRaise, setAmountToRaise] = useState('');
  const dispatch = useDispatch();
  const charityId = "your-charity-id"; // This should be dynamically assigned based on the logged-in user
  const charity = useSelector(state => state.charity.charityApplications.find(c => c.id === charityId));

  const handleApply = () => {
    if (!charityName || !charityDescription || !amountToRaise) {
      alert('Please fill out all fields.');
      return;
    }

    if (charity && charity.status !== 'approved') {
      alert('Your charity application has not been approved yet.');
      return;
    }

    dispatch(setCharityDetails({ id: charityId, name: charityName, description: charityDescription, amountToRaise }));
    alert('Charity application submitted!');
  };

  return (
    <div>
      <h2>Apply to be a Charity</h2>
      <input
        type="text"
        placeholder="Charity Name"
        value={charityName}
        onChange={(e) => setCharityName(e.target.value)}
        disabled={charity && charity.status !== 'approved'} // Disable input if not approved
      />
      <textarea
        placeholder="Charity Description"
        value={charityDescription}
        onChange={(e) => setCharityDescription(e.target.value)}
        disabled={charity && charity.status !== 'approved'} // Disable textarea if not approved
      />
      <input
        type="number"
        placeholder="Amount to Raise"
        value={amountToRaise}
        onChange={(e) => setAmountToRaise(e.target.value)}
        disabled={charity && charity.status !== 'approved'} // Disable input if not approved
      />
      <button onClick={handleApply} disabled={charity && charity.status !== 'approved'}>Submit Application</button>
      {charity && charity.status !== 'approved' && <p>Your application is not yet approved by the administrator.</p>}
    </div>
  );
};

export default CharityApplication;
