import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, donors, totalDonations, beneficiaries, inventory, stories } = useSelector(state => state.charity);
  const donationsByCharity = useSelector(state => state.donation.donationsByCharity);

  const [newStoryTitle, setNewStoryTitle] = useState('');
  const [newStoryContent, setNewStoryContent] = useState('');
  const [selectedCharityId, setSelectedCharityId] = useState('');
  const [newBeneficiaryName, setNewBeneficiaryName] = useState('');
  const [newBeneficiaryAge, setNewBeneficiaryAge] = useState('');

  useEffect(() => {
    // Simulate fetching data from API
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
    dispatch(setTotalDonations(300));
    dispatch(setInventory([{ id: 1, item: 'Food Packages', quantity: 50 }]));
  }, [dispatch]);

  const handleAddStory = () => {
    // Same as previous
  };

  const handleAddBeneficiary = () => {
    // Same as previous
  };

  // Get donations for the selected charity
  const charityDonations = donationsByCharity[selectedCharityId] || [];

  return (
    <div>
      <h2>Charity Dashboard</h2>

      <h3>Charities</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map(charity => (
            <li key={charity.id}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Status: {charity.status}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No charities available.</p>
      )}

      <h3>Donations</h3>
      {charityDonations.length > 0 ? (
        <ul>
          {charityDonations.map((donation, index) => (
            <li key={index}>
              <p>Amount: ${donation.amount}</p>
              <p>Date: {new Date(donation.date).toLocaleDateString()}</p>
              <p>Donor: {donation.isAnonymous ? 'Anonymous' : donation.userName}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No donations yet for this charity.</p>
      )}

      {/* Add Beneficiary and Story forms go here */}
    </div>
  );
};

export default CharityDashboard;
