import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  selectedCampaign: null,
  donationAmount: 0,
  recurring: false,
  anonymity: true,
  userName: '',
  donationHistory: [],
};

const donationSlice = createSlice({
  name: 'donation',
  initialState,
  reducers: {
    setSelectedCampaign: (state, action) => {
      state.selectedCampaign = action.payload;
    },
    setDonationAmount: (state, action) => {
      state.donationAmount = action.payload;
    },
    setRecurring: (state, action) => {
      state.recurring = action.payload;
    },
    setAnonymity: (state, action) => {
      state.anonymity = action.payload;
    },
    setUserName: (state, action) => {
      state.userName = action.payload;
    },
    addDonation: (state, action) => {
      state.donationHistory.push(action.payload); 
    },
  },
});

export const { 
  setSelectedCampaign, 
  setDonationAmount, 
  setRecurring, 
  setAnonymity, 
  setUserName,
  addDonation 
} = donationSlice.actions;

export default donationSlice.reducer;
