import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const drugCategories = {
  ByPurpose: [
    {
      title: "Analgesics",
      description: "Pain relievers such as ibuprofen and acetaminophen have been used for centuries to alleviate pain and discomfort. Their development has significantly improved the management of acute and chronic pain.",
      imgSrc: "path/to/analgesics.jpg", // Update with actual image path
    },
    // ... other categories
  ],
  ByForm: [
    {
      title: "Tablets/Capsules",
      description: "Solid forms taken orally.",
    },
    // ... other categories
  ],
  ByMethod: [
    {
      title: "Oral",
      description: "Taken by mouth.",
    },
    // ... other categories
  ],
  AlternativeAndComplementary: [
    {
      title: "Herbal Medicine",
      description: "Dating back thousands of years, herbal medicine utilizes plant extracts to treat various ailments, forming the basis of many traditional healing practices worldwide.",
      imgSrc: "path/to/herbal_medicine.jpg", // Update with actual image path
    },
    // ... other categories
  ],
};

const Home = () => {
  const [selectedCategory, setSelectedCategory] = useState('All');

  const buttonStyle = {
    margin: '10px',
    padding: '10px 20px',
    backgroundColor: 'green',
    color: 'white',
    textDecoration: 'none',
    borderRadius: '5px',
  };

  const containerStyle = {
    textAlign: 'center',
    marginTop: '50px',
    padding: '20px',
  };

  const categoryStyle = {
    marginTop: '40px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    padding: '10px',
  };

  const drugCardStyle = {
    border: '1px solid #ccc',
    borderRadius: '5px',
    margin: '20px',
    padding: '10px',
    display: 'inline-block',
    width: '250px',
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const filteredCategories = selectedCategory === 'All'
    ? Object.entries(drugCategories)
    : Object.entries(drugCategories).filter(([key]) => key === selectedCategory);

  return (
    <div style={containerStyle}>
      <h1>Welcome to Medical Reminder App</h1>
      <p>Track and manage your medical prescriptions and appointments easily.</p>
      <Link to="/add-reminder" style={buttonStyle}>Add a New Reminder</Link>
      <Link to="/reminders" style={buttonStyle}>View All Reminders</Link>
      <Link to="/login" style={buttonStyle}>Login</Link>

      <div>
        <label htmlFor="category-filter">Select a Category: </label>
        <select id="category-filter" value={selectedCategory} onChange={handleCategoryChange}>
          <option value="All">All</option>
          {Object.keys(drugCategories).map((category) => (
            <option key={category} value={category}>{category.replace(/([A-Z])/g, ' $1').trim()}</option>
          ))}
        </select>
      </div>

      {filteredCategories.map(([category, drugs]) => (
        <div key={category} style={categoryStyle}>
          <h2>{category.replace(/([A-Z])/g, ' $1').trim()}</h2>
          <div>
            {drugs.map((drug, index) => (
              <div key={index} style={drugCardStyle}>
                <h3>{drug.title}</h3>
                {drug.imgSrc && <img src={drug.imgSrc} alt={drug.title} style={{ width: '100%', borderRadius: '5px' }} />}
                <p>{drug.description}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Home;
