// Signup.js
import React from 'react';
import { useFormik } from 'formik';
import { useNavigate, Link } from 'react-router-dom';

const Signup = ({ onSignup }) => {
  const navigate = useNavigate();

  const formik = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      phone: '',
      gender: '',
    },
    onSubmit: (values) => {
      onSignup(values); // Call the onSignup function
      navigate('/login'); // Redirect to login after signup
    },
  });

  return (
    <div style={{ backgroundColor: "white", width: "30%", margin: "50px auto", padding: "40px", borderRadius: "10px", boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)" }}>
      <h3 style={{ textAlign: "center", color: "darkcyan" }}>Sign Up</h3>
      <form onSubmit={formik.handleSubmit} style={{ display: "flex", flexDirection: "column" }}>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          value={formik.values.name}
          onChange={formik.handleChange}
          placeholder="Enter your name"
          required
        />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={formik.values.email}
          onChange={formik.handleChange}
          placeholder="Enter your email"
          required
        />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={formik.values.password}
          onChange={formik.handleChange}
          placeholder="Enter your password"
          required
        />
        <label htmlFor="phone">Phone Number:</label>
        <input
          type="tel"
          id="phone"
          value={formik.values.phone}
          onChange={formik.handleChange}
          placeholder="Enter your phone number"
          required
        />
        <label htmlFor="gender">Gender:</label>
        <select
          id="gender"
          value={formik.values.gender}
          onChange={formik.handleChange}
          required
        >
          <option value="">Select your gender</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
          <option value="other">Other</option>
        </select>
        <button type="submit" style={{ marginTop: "15px", backgroundColor: "darkcyan", color: "white", border: "none", padding: "10px", cursor: "pointer", borderRadius: "5px", fontSize: "16px", fontWeight: "bold" }}>
          Sign Up
        </button>
      </form>
      <p style={{ textAlign: "center", marginTop: "20px" }}>
        Already have an account? <Link to="/login">Login</Link>
      </p>
    </div>
  );
};

export default Signup;
