// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const charitySlice = createSlice({
  name: 'charity',
  initialState: {
    charities: [],
    donors: [],
    totalDonations: 0,
    beneficiaries: [],
    inventory: [],
    stories: {},
    userRole: 'donor',  // Default role, change dynamically based on logged-in user
  },
  reducers: {
    setCharityDetails: (state, action) => {
      state.charities.push({ ...action.payload, status: 'pending' });
    },
    approveCharity: (state, action) => {
      const charity = state.charities.find(c => c.id === action.payload);
      if (charity) charity.status = 'approved';
    },
    rejectCharity: (state, action) => {
      const charity = state.charities.find(c => c.id === action.payload);
      if (charity) charity.status = 'rejected';
    },
    deleteCharity: (state, action) => {
      state.charities = state.charities.filter(c => c.id !== action.payload);
    },
    setUserRole: (state, action) => {
      state.userRole = action.payload;
    },
  },
});

export const {
  setCharityDetails,
  approveCharity,
  rejectCharity,
  deleteCharity,
  setUserRole,
} = charitySlice.actions;

export default charitySlice.reducer;
