import React, { useState, useEffect } from 'react';

// Custom hook to get window width
const useWindowWidth = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return windowWidth;
};

const Reminders = () => {
  const [reminders, setReminders] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentReminder, setCurrentReminder] = useState({ title: '', date: '', time: '', description: '' });

  const windowWidth = useWindowWidth(); // Get the current window width

  useEffect(() => {
    // Fetch data from local storage
    const savedReminders = JSON.parse(localStorage.getItem('reminders')) || [];
    setReminders(savedReminders);
  }, []);

  const handleEditClick = (reminder) => {
    setIsEditing(true);
    setCurrentReminder(reminder);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCurrentReminder({ ...currentReminder, [name]: value });
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    const updatedReminders = reminders.map((reminder) =>
      reminder.title === currentReminder.title ? currentReminder : reminder
    );
    setReminders(updatedReminders);
    localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    setIsEditing(false);
    setCurrentReminder({ title: '', date: '', time: '', description: '' }); // Reset the form
  };

  const handleDelete = (title) => {
    if (window.confirm(`Are you sure you want to delete the reminder titled "${title}"?`)) {
      const updatedReminders = reminders.filter((reminder) => reminder.title !== title);
      setReminders(updatedReminders);
      localStorage.setItem('reminders', JSON.stringify(updatedReminders));
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ fontSize: windowWidth < 600 ? '1.5em' : '2em' }}>Your Medical Reminders</h2>

      {isEditing ? (
        <div>
          <h3>Edit Reminder</h3>
          <form onSubmit={handleUpdate}>
            <input
              type="text"
              name="title"
              value={currentReminder.title}
              onChange={handleChange}
              placeholder="Title"
              required
              style={inputStyle}
            />
            <input
              type="date"
              name="date"
              value={currentReminder.date}
              onChange={handleChange}
              required
              style={inputStyle}
            />
            <input
              type="time"
              name="time"
              value={currentReminder.time}
              onChange={handleChange}
              required
              style={inputStyle}
            />
            <textarea
              name="description"
              value={currentReminder.description}
              onChange={handleChange}
              placeholder="Description"
              required
              style={inputStyle}
            />
            <button type="submit" style={buttonStyle}>Update Reminder</button>
          </form>
        </div>
      ) : (
        <div style={{ display: 'flex', flexWrap: 'wrap' }}>
          {reminders.length > 0 ? (
            reminders.map((reminder, index) => (
              <div key={index} style={{ ...cardStyle, width: windowWidth < 600 ? '100%' : '200px' }}>
                <h3>{reminder.title}</h3>
                <p>Date: {reminder.date}</p>
                <p>Time: {reminder.time}</p>
                <p>Description: {reminder.description}</p>
                <button onClick={() => handleEditClick(reminder)} style={{ ...editButtonStyle, fontSize: windowWidth < 600 ? '1.2em' : '1em' }}>
                  Edit
                </button>
                <button onClick={() => handleDelete(reminder.title)} style={{ ...deleteButtonStyle, fontSize: windowWidth < 600 ? '1.2em' : '1em' }}>
                  Delete
                </button>
              </div>
            ))
          ) : (
            <p>No reminders available. Please add one.</p>
          )}
        </div>
      )}
    </div>
  );
};

const cardStyle = {
  margin: '10px',
  padding: '15px',
  border: '1px solid #ddd',
  borderRadius: '5px',
  flexGrow: 1,
};

const inputStyle = {
  margin: '10px 0',
  padding: '10px',
  width: '100%',
  boxSizing: 'border-box',
};

const buttonStyle = {
  padding: '10px 20px',
  backgroundColor: 'green',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

const editButtonStyle = {
  marginTop: '10px',
  padding: '5px 10px',
  backgroundColor: 'orange',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  width: '100%', // Make the button take the full width of the card
};

const deleteButtonStyle = {
  marginTop: '10px',
  padding: '5px 10px',
  backgroundColor: 'red',
  color: 'white',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  width: '100%', // Make the button take the full width of the card
};

export default Reminders;
