// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  charities: [
    // Example structure for a charity
    {
      id: 1,
      name: 'Food for All',
      description: 'Providing food to underprivileged communities.',
      totalDonations: 0, // To track total donations for this charity
    },
  ],
};

const charitySlice = createSlice({
  name: 'charity',
  initialState,
  reducers: {
    // Action to add a donation to a charity
    addDonationToCharity: (state, action) => {
      const { charityId, amount } = action.payload;
      const charity = state.charities.find((charity) => charity.id === charityId);
      if (charity) {
        charity.totalDonations += amount;
      }
    },
    // Other actions like setting donors, inventory, etc.
  },
});

export const { addDonationToCharity } = charitySlice.actions;

export default charitySlice.reducer;
