// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const charitySlice = createSlice({
  name: 'charity',
  initialState: {
    charities: [],
    charityApplications: [], // Stores pending charity applications
    admins: [],  // This could be a list of admins
    currentUser: null, // Keeps track of the current logged-in user
    donors: [],
    totalDonations: 0,
    beneficiaries: [],
    inventory: [],
    stories: {}, 
  },
  reducers: {
    setCharityDetails: (state, action) => {
      state.charities.push(action.payload);
    },
    setCharityApplications: (state, action) => {
      state.charityApplications = action.payload;
    },
    approveCharityApplication: (state, action) => {
      const applicationIndex = state.charityApplications.findIndex(app => app.id === action.payload.id);
      if (applicationIndex >= 0) {
        const approvedApplication = state.charityApplications.splice(applicationIndex, 1)[0];
        state.charities.push(approvedApplication);  // Add to approved charities
      }
    },
    rejectCharityApplication: (state, action) => {
      const applicationIndex = state.charityApplications.findIndex(app => app.id === action.payload.id);
      if (applicationIndex >= 0) {
        state.charityApplications.splice(applicationIndex, 1); // Remove rejected charity
      }
    },
    deleteCharity: (state, action) => {
      const charityIndex = state.charities.findIndex(charity => charity.id === action.payload.id);
      if (charityIndex >= 0) {
        state.charities.splice(charityIndex, 1);  // Remove deleted charity
      }
    },
    setCurrentUser: (state, action) => {
      state.currentUser = action.payload;
    },
  },
});

export const {
  setCharityDetails,
  setCharityApplications,
  approveCharityApplication,
  rejectCharityApplication,
  deleteCharity,
  setCurrentUser,
} = charitySlice.actions;

export default charitySlice.reducer;
