import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, donors, totalDonations, inventory, stories } = useSelector(state => state.charity);

  const [newStoryTitle, setNewStoryTitle] = useState('');
  const [newStoryContent, setNewStoryContent] = useState('');
  const [selectedCharityId, setSelectedCharityId] = useState('');
  const [newBeneficiaryName, setNewBeneficiaryName] = useState('');
  const [newBeneficiaryAge, setNewBeneficiaryAge] = useState('');

  useEffect(() => {
    // Simulate fetching data from API
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
    dispatch(setTotalDonations(300));
    dispatch(setInventory([{ id: 1, item: 'Food Packages', quantity: 50 }]));
    dispatch(addBeneficiary({ charityId: 1, id: 1, name: 'Ben', age: 12 }));
    dispatch(addStory({ charityId: 1, title: 'Ben Gets a Food Package', content: 'Ben was very happy to receive a food package.' }));
  }, [dispatch]);

  const handleAddStory = () => {
    if (!newStoryTitle || !newStoryContent || !selectedCharityId) {
      alert('Please fill out all fields: title, content, and select a charity.');
      return;
    }

    dispatch(addStory({ charityId: selectedCharityId, title: newStoryTitle, content: newStoryContent }));
    setNewStoryTitle('');
    setNewStoryContent('');
    alert('Story added successfully!');
  };

  const handleAddBeneficiary = () => {
    if (!newBeneficiaryName || !newBeneficiaryAge || !selectedCharityId) {
      alert('Please fill out all fields: name, age, and select a charity.');
      return;
    }

    dispatch(addBeneficiary({
      charityId: selectedCharityId,
      name: newBeneficiaryName,
      age: newBeneficiaryAge
    }));

    setNewBeneficiaryName('');
    setNewBeneficiaryAge('');
    alert('Beneficiary added successfully!');
  };

  const selectedCharity = charities.find(charity => charity.id === parseInt(selectedCharityId));

  return (
    <div>
      <h2>Charity Dashboard</h2>

      <h3>Charities</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map(charity => (
            <li key={charity.id}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Amount to raise: ${charity.amountToRaise}</p>
              <button onClick={() => setSelectedCharityId(charity.id)}>View Charity</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No charities available.</p>
      )}

      {/* Only show charity details when a charity is selected */}
      {selectedCharityId && selectedCharity && (
        <div>
          <h3>{selectedCharity.name} Details</h3>

          {/* Display Beneficiaries of Selected Charity */}
          <h4>Beneficiaries</h4>
          {selectedCharity.beneficiaries && selectedCharity.beneficiaries.length > 0 ? (
            <ul>
              {selectedCharity.beneficiaries.map((beneficiary, index) => (
                <li key={index}>
                  <strong>{beneficiary.name}</strong> (Age: {beneficiary.age})
                </li>
              ))}
            </ul>
          ) : (
            <p>No beneficiaries yet.</p>
          )}

          {/* Add Beneficiary Form */}
          <h4>Add a Beneficiary</h4>
          <div>
            <input
              type="text"
              placeholder="Beneficiary Name"
              value={newBeneficiaryName}
              onChange={(e) => setNewBeneficiaryName(e.target.value)}
            />
            <input
              type="number"
              placeholder="Beneficiary Age"
              value={newBeneficiaryAge}
              onChange={(e) => setNewBeneficiaryAge(e.target.value)}
            />
            <button onClick={handleAddBeneficiary}>Add Beneficiary</button>
          </div>

          {/* Display Stories of Selected Charity */}
          <h4>Stories</h4>
          {stories[selectedCharityId] && stories[selectedCharityId].length > 0 ? (
            <ul>
              {stories[selectedCharityId].map((story, index) => (
                <li key={index}>
                  <h5>{story.title}</h5>
                  <p>{story.content}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No stories for this charity yet.</p>
          )}

          {/* Add Story Form */}
          <h4>Add a Story</h4>
          <div>
            <input
              type="text"
              placeholder="Story Title"
              value={newStoryTitle}
              onChange={(e) => setNewStoryTitle(e.target.value)}
            />
            <textarea
              placeholder="Story Content"
              value={newStoryContent}
              onChange={(e) => setNewStoryContent(e.target.value)}
            />
            <button onClick={handleAddStory}>Post Story</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CharityDashboard;
