// CharityDashboard.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, donors, totalDonations, beneficiaries, inventory, stories } = useSelector(state => state.charity);

  useEffect(() => {
    // Simulate fetching data from API (this could be from a real backend)
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
    dispatch(setTotalDonations(300));
    dispatch(setInventory([{ id: 1, item: 'Food Packages', quantity: 50 }]));
    dispatch(addBeneficiary({ id: 1, name: 'Ben', age: 12 }));
    dispatch(addStory({ title: 'Ben Gets a Food Package', content: 'Ben was very happy to receive a food package.' }));
  }, [dispatch]);

  return (
    <div>
      <h2>Charity Dashboard</h2>

      <h3>Charities</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map((charity) => (
            <li key={charity.id}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Total Donations: ${charity.totalDonations}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No charities available.</p>
      )}

      {/* Other sections like donors, inventory, beneficiaries, and stories */}
    </div>
  );
};

export default CharityDashboard;
