// AdminDashboard.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { approveCharity, rejectCharity, deleteCharity } from '../redux/charitySlice';

const AdminDashboard = () => {
  const dispatch = useDispatch();
  const { charities } = useSelector(state => state.charity);

  const handleApprove = (charityId) => {
    dispatch(approveCharity(charityId));
  };

  const handleReject = (charityId) => {
    dispatch(rejectCharity(charityId));
  };

  const handleDelete = (charityId) => {
    dispatch(deleteCharity(charityId));
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <h3>Pending Charity Applications</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.filter(c => c.status === 'pending').map(charity => (
            <li key={charity.id}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Status: {charity.status}</p>
              <button onClick={() => handleApprove(charity.id)}>Approve</button>
              <button onClick={() => handleReject(charity.id)}>Reject</button>
              <button onClick={() => handleDelete(charity.id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No pending applications.</p>
      )}
    </div>
  );
};

export default AdminDashboard;
