import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ user }) => {
  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-around',
    backgroundColor: '#282c34',
    padding: '15px',
  };

  const linkStyle = {
    color: 'white',
    textDecoration: 'none',
    fontSize: '18px',
    transition: 'color 0.3s',
  };

  const handleMouseEnter = (e) => {
    e.target.style.color = '#61dafb';
  };

  const handleMouseLeave = (e) => {
    e.target.style.color = 'white';
  };

  return (
    <nav style={navbarStyle}>
      <Link to="/" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Home</Link>
      <Link to="/add-reminder" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Add Medication Reminder</Link>
      <Link to="/reminders" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Medication Reminders</Link>
      <Link to="/add-appointment" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Add Appointment Reminder</Link>
      <Link to="/appointments" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Appointment Reminders</Link>
      <Link to="/calendar" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Calendar</Link>
      {user ? (
        <span style={linkStyle}>Welcome, {user.name}</span> // Display user name
      ) : (
        <Link to="/login" style={linkStyle} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>Login</Link>
      )}
    </nav>
  );
};

export default Navbar;
