import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations } from '../redux/charitySlice'; // Add actions for Charity Dashboard

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, donors, totalDonations } = useSelector(state => state.charity);

  useEffect(() => {
    // Simulate fetching data from API or updating the charity donors and donations
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
    dispatch(setTotalDonations(300));  // Initial donations

  }, [dispatch]);

  return (
    <div>
      <h2>Charity Dashboard</h2>
      <h3>Donors</h3>
      <ul>
        {donors.map(donor => (
          <li key={donor.id}>
            {donor.name} donated ${donor.amount} {donor.anonymous ? '(Anonymous)' : ''}
          </li>
        ))}
      </ul>

      <h3>Total Donations: ${totalDonations}</h3>

      <h3>Campaigns</h3>
      {charities.map(charity => (
        <div key={charity.name}>
          <h4>{charity.name}</h4>
          <p>Total Raised: ${charity.amountRaised}</p>
        </div>
      ))}
    </div>
  );
};

export default CharityDashboard;
