import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  charities: [],
  donors: [],
  totalDonations: 0,
};

const charitySlice = createSlice({
  name: 'charity',
  initialState,
  reducers: {
    setDonors: (state, action) => {
      state.donors = action.payload;
    },
    setTotalDonations: (state, action) => {
      state.totalDonations = action.payload;
    },
    addDonor: (state, action) => {
      state.donors.push(action.payload);
    },
    updateTotalDonations: (state, action) => {
      state.totalDonations += action.payload;
    },
  },
});

export const { setDonors, setTotalDonations, addDonor, updateTotalDonations } = charitySlice.actions;

export default charitySlice.reducer;
