// DonationHistory.jsx
import React, { useState, useEffect } from 'react';

const DonationHistory = () => {
  const [donationHistory, setDonationHistory] = useState([]);
  const [stories, setStories] = useState({});

  // Fetch donation history from localStorage (or global state if using Redux)
  useEffect(() => {
    const donations = JSON.parse(localStorage.getItem('donations')) || [];
    setDonationHistory(donations);

    // Fetch campaign stories (This can be from an API or a static object)
    const campaignStories = {
      'Help Build a School': 'The construction of the school has begun, and we are making great progress!',
      'Feed the Hungry': 'Your donations have helped provide 1000 meals to the homeless last week.',
      'Support Medical Aid': 'We are able to distribute medical supplies to rural areas thanks to your generosity.',
    };
    setStories(campaignStories);
  }, []);

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Your Donation History</h2>
      {donationHistory.length > 0 ? (
        <ul>
          {donationHistory.map((donation, index) => (
            <li key={index} style={styles.donationItem}>
              <h3>{donation.campaignTitle}</h3>
              <p>Amount Donated: ${donation.amount}</p>
              <p>Date: {new Date(donation.date).toLocaleDateString()}</p>
              {donation.isAnonymous ? (
                <p>Anonymous Donation</p>
              ) : (
                <p>Donor: {donation.userName}</p>
              )}
              {/* Display the campaign update */}
              {stories[donation.campaignTitle] && (
                <div>
                  <h4>Campaign Update:</h4>
                  <p>{stories[donation.campaignTitle]}</p>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>You haven't made any donations yet.</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    maxWidth: '600px',
    margin: 'auto',
  },
  title: {
    textAlign: 'center',
    color: '#007bff',
  },
  donationItem: {
    margin: '15px 0',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: 'inset 0 2px 5px rgba(0, 0, 0, 0.1)',
  },
};

export default DonationHistory;
