import React, { useState } from 'react';

function DonateForm() {
  const [donationAmount, setDonationAmount] = useState('');
  const [isAnonymous, setIsAnonymous] = useState(false);
  const [donorName, setDonorName] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate that a name is provided when not anonymous
    if (!isAnonymous && !donorName.trim()) {
      alert('Please provide your name if you are not donating anonymously.');
      return;
    }

    const donationData = {
      amount: donationAmount,
      anonymous: isAnonymous,
      name: isAnonymous ? null : donorName,
    };

    // Process the donation data (e.g., dispatch an action or send to backend)
    console.log(donationData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Donation Amount:
        <input
          type="number"
          value={donationAmount}
          onChange={(e) => setDonationAmount(e.target.value)}
          required
        />
      </label>

      <label>
        Remain Anonymous:
        <input
          type="checkbox"
          checked={isAnonymous}
          onChange={(e) => setIsAnonymous(e.target.checked)}
        />
      </label>

      {!isAnonymous && (
        <label>
          Your Name:
          <input
            type="text"
            value={donorName}
            onChange={(e) => setDonorName(e.target.value)}
            required
          />
        </label>
      )}

      <button type="submit">Donate</button>
    </form>
  );
}

export default DonateForm;
