// src/redux/store.js
import { configureStore } from '@reduxjs/toolkit';
import charityReducer from './charitySlice';
import donationReducer from './donationSlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charityDetails, donors, totalDonations, beneficiaries, inventory, stories } = useSelector(state => state.charity);

  useEffect(() => {
    // Simulate fetching data from API
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));

    dispatch(setTotalDonations(300));
    dispatch(setInventory([{ id: 1, item: 'Food Packages', quantity: 50 }]));
    dispatch(addBeneficiary({ id: 1, name: 'Ben', age: 12 }));
    dispatch(addStory({ title: 'Ben Gets a Food Package', content: 'Ben was very happy to receive a food package.' }));
  }, [dispatch]);

  return (
    <div>
      <h2>Charity Dashboard</h2>
      {charityDetails && (
        <>
          <h3>{charityDetails.name}</h3>
          <p>{charityDetails.description}</p>
        </>
      )}

      <h4>Donors</h4>
      <ul>
        {donors.map(donor => (
          <li key={donor.id}>
            {donor.anonymous ? 'Anonymous Donor' : donor.name} donated ${donor.amount}
          </li>
        ))}
      </ul>

      <h4>Total Donations: ${totalDonations}</h4>

      <h4>Beneficiaries</h4>
      <ul>
        {beneficiaries.map(beneficiary => (
          <li key={beneficiary.id}>
            {beneficiary.name} (Age: {beneficiary.age})
          </li>
        ))}
      </ul>

      <h4>Stories</h4>
      <ul>
        {stories.map((story, index) => (
          <li key={index}>
            <h5>{story.title}</h5>
            <p>{story.content}</p>
          </li>
        ))}
      </ul>

      <h4>Inventory</h4>
      <ul>
        {inventory.map(item => (
          <li key={item.id}>
            {item.item}: {item.quantity} units
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CharityDashboard;
