import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CalendarView from './CalendarView'; // Calendar with reminders
import DonationList from './DonationList'; // Campaign listing
import DonationHistory from './DonationHistory'; // History of donations
import DonateForm from './DonateForm'; // Form for donation
import { useSelector } from 'react-redux';

const Home = () => {
  const [showCalendar, setShowCalendar] = useState(false);
  const [showDonationHistory, setShowDonationHistory] = useState(false);
  const [showDonationForm, setShowDonationForm] = useState(false);
  const selectedCampaign = useSelector(state => state.donation.selectedCampaign);

  return (
    <div>
      <h1>Welcome to Our Donation Platform</h1>
      <p>Your contributions can make a difference!</p>
      <p>Browse our campaigns and make a donation today.</p>

      <nav>
        <ul>
          <li>
            <Link to="/" onClick={() => setShowCalendar(false)}>Home</Link>
          </li>
          <li>
            <Link to="/donations" onClick={() => setShowDonationForm(true)}>Donate</Link>
          </li>
          <li>
            <Link to="/donation-history" onClick={() => setShowDonationHistory(true)}>Donation History</Link>
          </li>
        </ul>
      </nav>

      {/* Display Calendar with Reminders */}
      {showCalendar && <CalendarView />}

      {/* Display Donation Campaigns */}
      {!showDonationHistory && !showDonationForm && <DonationList />}

      {/* Donation Form */}
      {showDonationForm && selectedCampaign && <DonateForm />}

      {/* Donation History */}
      {showDonationHistory && <DonationHistory />}

      {/* Display a message when no campaign is selected */}
      {!selectedCampaign && <p>Please select a campaign to donate to.</p>}
    </div>
  );
};

export default Home;
