import React from 'react';
import { Link } from 'react-router-dom';

const Login = () => {
  const styles = {
    container: {
      backgroundColor: "white",
      width: "90%", // Adjust to 90% for smaller screens
      maxWidth: "400px", // Set a max width for larger screens
      margin: "50px auto",
      padding: "40px",
      borderRadius: "10px",
      boxShadow: "0 4px 20px rgba(0, 0, 0, 0.1)",
      transition: "box-shadow 0.3s",
    },
    title: {
      textAlign: "center",
      fontWeight: "bold",
      color: "darkcyan",
      textTransform: "uppercase",
      fontSize: "1.5em", // Responsive title size
    },
    form: {
      display: "flex",
      flexDirection: "column",
    },
    label: {
      marginTop: "15px",
      fontWeight: "bold",
      color: "#333",
    },
    input: {
      marginTop: "5px",
      padding: "10px",
      borderRadius: "5px",
      border: "1px solid #ccc",
      transition: "border 0.3s",
      fontSize: "1em", // Responsive input size
    },
    button: {
      marginTop: "15px",
      backgroundColor: "darkcyan",
      color: "white",
      border: "none",
      padding: "10px",
      cursor: "pointer",
      borderRadius: "5px",
      fontSize: "1em", // Responsive button size
      fontWeight: "bold",
      transition: "background-color 0.3s",
    },
    link: {
      marginTop: "20px",
      textAlign: "center",
      fontSize: "0.9em", // Smaller font size for the link
    },
  };

  return (
    <div style={styles.container}>
      <h3 style={styles.title}>Login</h3>
      <form style={styles.form}>
        <label htmlFor="email" style={styles.label}>Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          autoComplete="off"
          placeholder="Enter your Email"
          style={styles.input}
          onFocus={(e) => e.target.style.border = "1px solid darkcyan"}
          onBlur={(e) => e.target.style.border = "1px solid #ccc"}
        />
        <label htmlFor="password" style={styles.label}>Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          autoComplete="off"
          placeholder="Enter your Password"
          style={styles.input}
          onFocus={(e) => e.target.style.border = "1px solid darkcyan"}
          onBlur={(e) => e.target.style.border = "1px solid #ccc"}
        />
        <button 
          type="submit" 
          style={styles.button}
        >
          Login
        </button>
      </form>
      <div style={styles.link}>
        <p>Don't have an account? <Link to="/signup">Sign Up</Link></p>
      </div>
    </div>
  );
};

export default Login;
