import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useDispatch } from 'react-redux';
import { addDonationToCharity } from '../redux/charitySlice'; // Action to update charity donations

const CheckoutForm = ({ donationAmount, campaignTitle, isAnonymous, userName, beneficiaryStories, charityId }) => {
  const stripe = useStripe();
  const elements = useElements();
  const dispatch = useDispatch();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);

    const cardElement = elements.getElement(CardElement);

    // Confirm the payment with Stripe
    const { error, paymentIntent } = await stripe.confirmCardPayment(
      'your-client-secret-from-backend', // Replace with the actual client secret from your backend
      {
        payment_method: {
          card: cardElement,
        },
      }
    );

    if (error) {
      setError(error.message);
    } else {
      // Create the donation object to store in localStorage and Redux
      const newDonation = {
        campaignTitle,
        amount: donationAmount,
        date: new Date().toISOString(),
        isAnonymous,
        userName,
        beneficiaryStories,
        charityId, // Track the charity to update its donation total
      };

      // Update the donation history in localStorage
      const donationHistory = JSON.parse(localStorage.getItem('donations')) || [];
      localStorage.setItem('donations', JSON.stringify([...donationHistory, newDonation]));

      // Dispatch the action to update the charity's donation total in Redux
      dispatch(addDonationToCharity({ charityId, amount: donationAmount }));

      console.log('Payment succeeded!', paymentIntent);
      setError(null);
    }

    setProcessing(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Credit Card Details
        <CardElement />
      </label>
      <br />
      <button type="submit" disabled={!stripe || processing}>
        {processing ? 'Processing...' : `Donate $${donationAmount}`}
      </button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default CheckoutForm;
