import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { approveCharity, rejectCharity, deleteCharity } from '../redux/charitySlice';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const charities = useSelector(state => state.charity.charityApplications);

  const handleApprove = (id) => {
    dispatch(approveCharity(id));
  };

  const handleReject = (id) => {
    dispatch(rejectCharity(id));
  };

  const handleDelete = (id) => {
    dispatch(deleteCharity(id));
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <h3>Review Charity Applications</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map((charity) => (
            <li key={charity.id}>
              <h4>{charity.name}</h4>
              <p>{charity.description}</p>
              <p>Status: {charity.status}</p>
              {/* Approve Button */}
              <button
                onClick={() => handleApprove(charity.id)}
                disabled={charity.status !== 'pending'} // Only enable if the status is 'pending'
              >
                Approve
              </button>
              {/* Reject Button */}
              <button
                onClick={() => handleReject(charity.id)}
                disabled={charity.status !== 'pending'} // Only enable if the status is 'pending'
              >
                Reject
              </button>
              {/* Delete Button (always enabled) */}
              <button onClick={() => handleDelete(charity.id)}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No pending charity applications.</p>
      )}
    </div>
  );
};

export default AdminDashboard;
