import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonors, setTotalDonations, setInventory, addBeneficiary, addStory } from '../redux/charitySlice';

const CharityDashboard = () => {
  const dispatch = useDispatch();
  const { charities, donationsByCharity } = useSelector(state => state.charity);

  const [newStoryTitle, setNewStoryTitle] = useState('');
  const [newStoryContent, setNewStoryContent] = useState('');
  const [selectedCharityId, setSelectedCharityId] = useState('');
  const [newBeneficiaryName, setNewBeneficiaryName] = useState('');
  const [newBeneficiaryAge, setNewBeneficiaryAge] = useState('');

  useEffect(() => {
    // Simulate fetching data from API
    dispatch(setDonors([
      { id: 1, name: 'John Doe', amount: 100, anonymous: false },
      { id: 2, name: 'Jane Doe', amount: 200, anonymous: true },
    ]));
  }, [dispatch]);

  const totalDonationsForCharity = (charityId) => {
    return donationsByCharity[charityId] || 0;
  };

  return (
    <div>
      <h2>Charity Dashboard</h2>

      <h3>Charities</h3>
      {charities.length > 0 ? (
        <ul>
          {charities.map((charity) => (
            <li key={charity.id}>
              {charity.name}
              <br />
              <strong>Total Donations: </strong>
              ${totalDonationsForCharity(charity.id)}
            </li>
          ))}
        </ul>
      ) : (
        <p>No approved charities available</p>
      )}
    </div>
  );
};

export default CharityDashboard;
