// charitySlice.js
import { createSlice } from '@reduxjs/toolkit';

const charitySlice = createSlice({
  name: 'charity',
  initialState: {
    charities: [], // Approved charities
    charityDonations: {}, // Store donations for each charity by id
  },
  reducers: {
    // Existing actions...

    addDonationToCharity: (state, action) => {
      const { charityId, donation } = action.payload;

      // If no donations for this charity, initialize it
      if (!state.charityDonations[charityId]) {
        state.charityDonations[charityId] = [];
      }

      // Add the new donation to the charity's donations list
      state.charityDonations[charityId].push(donation);
    },
  },
});

export const { 
  addDonationToCharity,
  // Other actions...
} = charitySlice.actions;

export default charitySlice.reducer;
