// src/store/donationSlice.js
import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  selectedCampaign: null,
  donationAmount: 0,
  recurring: false,
  anonymity: false,
};

const donationSlice = createSlice({
  name: 'donation',
  initialState,
  reducers: {
    setSelectedCampaign: (state, action) => {
      state.selectedCampaign = action.payload;
    },
    setDonationAmount: (state, action) => {
      state.donationAmount = action.payload;
    },
    setRecurring: (state, action) => {
      state.recurring = action.payload;
    },
    setAnonymity: (state, action) => {
      state.anonymity = action.payload;
    },
  },
});

export const { setSelectedCampaign, setDonationAmount, setRecurring, setAnonymity } = donationSlice.actions;
export default donationSlice.reducer;
