import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Provider } from 'react-redux';
import store from './store';
import Navbar from './components/Navbar';
import Home from './components/Home';
import DonationList from './components/DonationList';
import DonateForm from './components/DonateForm';
import DonationHistory from './components/DonationHistory';
import CharityApplication from './components/CharityApplication';
import CharityDashboard from './components/CharityDashboard';
import './App.css';

function App() {
  const [userType, setUserType] = useState('donor'); // Track user type: 'donor' or 'charity'

  const handleUserTypeChange = (type) => {
    setUserType(type);
  };

  return (
    <Provider store={store}>
      <Router>
        <div className="App">
          <Navbar />

          {/* User type selection */}
          <div style={{ marginBottom: '20px' }}>
            <button onClick={() => handleUserTypeChange('donor')}>Switch to Donor</button>
            <button onClick={() => handleUserTypeChange('charity')}>Switch to Charity</button>
          </div>

          <Routes>
            <Route path="/" element={<Home />} />

            {/* Conditional rendering based on user type */}
            {userType === 'donor' ? (
              <>
                <Route path="/donations" element={<DonationList />} />
                <Route path="/donate" element={<DonateForm />} />
                <Route path="/donation-history" element={<DonationHistory />} />
              </>
            ) : (
              <>
                <Route path="/charity-application" element={<CharityApplication />} />
                <Route path="/charity-dashboard" element={<CharityDashboard />} />
              </>
            )}
          </Routes>
        </div>
      </Router>
    </Provider>
  );
}

export default App;
