import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const buttonStyle = {
    margin: '10px',
    padding: '10px 20px',
    backgroundColor: 'green',
    color: 'white',
    textDecoration: 'none',
    borderRadius: '5px',
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Welcome to Medical Reminder App</h1>
      <p>Track and manage your medical prescriptions and appointments easily.</p>
      <Link to="/add-reminder" style={buttonStyle}>Add a New Reminder</Link>
      <Link to="/reminders" style={buttonStyle}>View All Reminders</Link>
      <Link to="/login" style={buttonStyle}>Login</Link> {/* Link to Login page */}
    </div>
  );
};

export default Home;
