import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const CheckoutForm = ({ donationAmount, campaignTitle, isAnonymous, userName, onSuccessfulDonation }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);

    const cardElement = elements.getElement(CardElement);

    const { error, paymentIntent } = await stripe.confirmCardPayment(
      'your-client-secret-from-backend',
      {
        payment_method: {
          card: cardElement,
        },
      }
    );

    if (error) {
      setError(error.message);
    } else {
      const donationData = {
        campaignTitle,
        amount: donationAmount,
        date: new Date().toISOString(),
        isAnonymous,
        userName,  
        beneficiaryStories: [],  // Or use the appropriate beneficiary stories
      };

      // Call the onSuccessfulDonation callback passed from the parent
      onSuccessfulDonation(donationData);

      console.log('Payment succeeded!', paymentIntent);
      setError(null);
    }

    setProcessing(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Credit Card Details
        <CardElement />
      </label>
      <br />
      <button type="submit" disabled={!stripe || processing}>
        {processing ? 'Processing...' : `Donate $${donationAmount}`}
      </button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default CheckoutForm;
