// src/components/DonateForm.js
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonationAmount, setRecurring, setAnonymity, setUserName } from '../store/donationSlice'; // Assuming you have actions for setting the name
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import CheckoutForm from './CheckoutForm'; // Create a checkout form for Stripe

const stripePromise = loadStripe('your-publishable-key-from-stripe');

const DonateForm = () => {
  const dispatch = useDispatch();
  const selectedCampaign = useSelector(state => state.donation.selectedCampaign);
  const [amount, setAmount] = useState('');
  const [recurring, setRecurring] = useState(false);
  const [anonymity, setAnonymity] = useState(true);  // Default to anonymous
  const [userName, setUserName] = useState('');

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
    dispatch(setDonationAmount(e.target.value));
  };

  const handleRecurringChange = (e) => {
    setRecurring(e.target.checked);
    dispatch(setRecurring(e.target.checked));
  };

  const handleAnonymityChange = (e) => {
    setAnonymity(e.target.checked);
    dispatch(setAnonymity(e.target.checked));

    // If switching to anonymous, reset the userName to an empty string
    if (e.target.checked) {
      setUserName('');
      dispatch(setUserName('')); // Clear the name in the store
    }
  };

  const handleNameChange = (e) => {
    setUserName(e.target.value);
    dispatch(setUserName(e.target.value));
  };

  return (
    <div>
      <h2>Donate to {selectedCampaign.title}</h2>
      <form>
        <label>
          Donation Amount ($):
          <input type="number" value={amount} onChange={handleAmountChange} required />
        </label>
        <br />
        <label>
          Recurring Donation:
          <input type="checkbox" checked={recurring} onChange={handleRecurringChange} />
        </label>
        <br />
        <label>
          Anonymity:
          <input type="checkbox" checked={anonymity} onChange={handleAnonymityChange} />
        </label>
        <br />

        {/* Show the name input field only if anonymity is not checked */}
        {!anonymity && (
          <div>
            <label>
              Your Name:
              <input 
                type="text" 
                value={userName} 
                onChange={handleNameChange} 
                placeholder="Enter your name"
              />
            </label>
            <br />
          </div>
        )}

        <Elements stripe={stripePromise}>
          <CheckoutForm donationAmount={amount} userName={userName} />
        </Elements>
      </form>
    </div>
  );
};

export default DonateForm;
