import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { approveCharityApplication, rejectCharityApplication, deleteCharity, setCharityApplications } from '../redux/charitySlice';

const AdminDashboard = () => {
  const dispatch = useDispatch();
  const { charityApplications, charities } = useSelector(state => state.charity);
  const { currentUser } = useSelector(state => state.charity);

  useEffect(() => {
    if (currentUser && currentUser.role === 'admin') {
      // Fetch charity applications (this can be fetched from an API or mock data)
      dispatch(setCharityApplications([
        { id: 1, name: 'Charity A', description: 'Description of Charity A', status: 'pending' },
        { id: 2, name: 'Charity B', description: 'Description of Charity B', status: 'pending' },
      ]));
    }
  }, [dispatch, currentUser]);

  const handleApprove = (applicationId) => {
    dispatch(approveCharityApplication({ id: applicationId }));
  };

  const handleReject = (applicationId) => {
    dispatch(rejectCharityApplication({ id: applicationId }));
  };

  const handleDelete = (charityId) => {
    dispatch(deleteCharity({ id: charityId }));
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      {currentUser && currentUser.role === 'admin' ? (
        <>
          <h3>Pending Charity Applications</h3>
          {charityApplications.length > 0 ? (
            <ul>
              {charityApplications.map((application) => (
                <li key={application.id}>
                  <h4>{application.name}</h4>
                  <p>{application.description}</p>
                  <button onClick={() => handleApprove(application.id)}>Approve</button>
                  <button onClick={() => handleReject(application.id)}>Reject</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No pending applications.</p>
          )}

          <h3>Existing Charities</h3>
          {charities.length > 0 ? (
            <ul>
              {charities.map((charity) => (
                <li key={charity.id}>
                  <h4>{charity.name}</h4>
                  <button onClick={() => handleDelete(charity.id)}>Delete Charity</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No charities available.</p>
          )}
        </>
      ) : (
        <p>You do not have admin access.</p>
      )}
    </div>
  );
};

export default AdminDashboard;
