// CheckoutForm.js
import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useDispatch } from 'react-redux';
import { setTotalDonations, addDonor, addStory, addBeneficiary } from '../redux/charitySlice';

const CheckoutForm = ({ donationAmount, campaignTitle, isAnonymous, userName, beneficiaryStories, charityId }) => {
  const stripe = useStripe();
  const elements = useElements();
  const dispatch = useDispatch();
  const [processing, setProcessing] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setProcessing(true);

    const cardElement = elements.getElement(CardElement);

    const { error, paymentIntent } = await stripe.confirmCardPayment(
      'your-client-secret-from-backend',
      {
        payment_method: {
          card: cardElement,
        },
      }
    );

    if (error) {
      setError(error.message);
    } else {
      const newDonation = {
        campaignTitle,
        amount: donationAmount,
        date: new Date().toISOString(),
        isAnonymous,
        userName,
        beneficiaryStories,
        charityId, // Add the charityId for the campaign the donation was made to
      };

      // Save donation to local storage
      const donationHistory = JSON.parse(localStorage.getItem('donations')) || [];
      localStorage.setItem('donations', JSON.stringify([...donationHistory, newDonation]));

      // Update charity dashboard (Redux state)
      dispatch(addDonor({ amount: donationAmount, anonymous: isAnonymous, userName }));
      dispatch(setTotalDonations(paymentIntent.amount_received / 100)); // Assuming amount_received is in cents

      if (beneficiaryStories) {
        beneficiaryStories.forEach(story => {
          dispatch(addStory({ title: `Story from ${userName}`, content: story }));
        });
      }

      console.log('Payment succeeded!', paymentIntent);
      setError(null);
    }

    setProcessing(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Credit Card Details
        <CardElement />
      </label>
      <br />
      <button type="submit" disabled={!stripe || processing}>
        {processing ? 'Processing...' : `Donate $${donationAmount}`}
      </button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default CheckoutForm;
