import React, { useState, useEffect } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const CalendarView = () => {
  const [date, setDate] = useState(new Date());
  const [reminders, setReminders] = useState([]);

  useEffect(() => {
    const savedReminders = JSON.parse(localStorage.getItem('reminders')) || [];
    setReminders(savedReminders);
  }, []);

  const handleDateChange = (date) => {
    setDate(date);
  };

  const remindersForSelectedDate = reminders.filter(reminder => 
    new Date(reminder.date).toDateString() === date.toDateString()
  );

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2>Your Medication Schedule</h2>
      <Calendar onChange={handleDateChange} value={date} />
      <h3 style={{ marginTop: '20px' }}>Reminders for {date.toDateString()}:</h3>
      {remindersForSelectedDate.length > 0 ? (
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {remindersForSelectedDate.map((reminder, index) => (
            <li key={index} style={{ marginBottom: '15px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', backgroundColor: '#f9f9f9' }}>
              <strong style={{ fontSize: '18px' }}>{reminder.title}</strong><br />
              <span style={{ fontSize: '14px', color: '#555' }}>Time: {reminder.time}</span><br />
              <span style={{ fontSize: '14px', color: '#555' }}>Description: {reminder.description}</span>
            </li>
          ))}
        </ul>
      ) : (
        <p style={{ fontStyle: 'italic', color: '#777' }}>You have no reminders scheduled for this date. Consider adding some!</p>
      )}
    </div>
  );
};

export default CalendarView;
