import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setDonationAmount, setRecurring, setAnonymity, setUserName } from '../redux/donationSlice';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';
import CheckoutForm from './CheckoutForm';
import { addDonation } from '../redux/charitySlice';

const stripePromise = loadStripe('your-publishable-key-from-stripe');

const DonateForm = () => {
  const dispatch = useDispatch();
  const selectedCampaign = useSelector(state => state.donation.selectedCampaign);
  const [amount, setAmount] = useState('');
  const [recurring, setRecurring] = useState(false);
  const [anonymity, setAnonymity] = useState(true);  // Default to anonymous
  const [userName, setUserName] = useState('');

  const handleRecurringChange = (e) => { setRecurring(e.target.checked); };
  const handleAnonymityChange = (e) => { setAnonymity(e.target.checked); };
  const handleNameChange = (e) => { setUserName(e.target.value); };

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
    dispatch(setDonationAmount(e.target.value));
  };

  const campaignTitle = selectedCampaign ? selectedCampaign.title : "Loading..."; 

  const handleDonationSuccess = (paymentIntent) => {
    // Dispatch the donation to update the charity's donation total
    dispatch(addDonation({
      charityId: selectedCampaign.id,
      amount: parseFloat(amount),
    }));

    // You can also handle further actions like updating the UI or redirecting the user.
  };

  return (
    <div>
      <h2>Donate to {campaignTitle}</h2>
      <form>
        <label>
          Donation Amount ($):
          <input type="number" value={amount} onChange={handleAmountChange} required />
        </label>
        <br />
        <label>
          Recurring Donation:
          <input type="checkbox" checked={recurring} onChange={handleRecurringChange} />
        </label>
        <br />
        <label>
          Anonymity:
          <input type="checkbox" checked={anonymity} onChange={handleAnonymityChange} />
        </label>
        <br />
        {!anonymity && (
          <div>
            <label>
              Your Name:
              <input 
                type="text" 
                value={userName} 
                onChange={handleNameChange} 
                placeholder="Enter your name"
              />
            </label>
            <br />
          </div>
        )}

        <Elements stripe={stripePromise}>
          <CheckoutForm donationAmount={amount} userName={userName} onSuccess={handleDonationSuccess} />
        </Elements>
      </form>
    </div>
  );
};

export default DonateForm;
