import React from 'react';

const DonationHistory = () => {
  // Fetch donation history from localStorage (or global state if using Redux)
  const donationHistory = JSON.parse(localStorage.getItem('donations')) || [];

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Your Donation History</h2>
      {donationHistory.length > 0 ? (
        <ul>
          {donationHistory.map((donation, index) => (
            <li key={index} style={styles.donationItem}>
              <h3>{donation.campaignTitle}</h3>
              <p>Amount Donated: ${donation.amount}</p>
              <p>Date: {new Date(donation.date).toLocaleDateString()}</p>
              {donation.isAnonymous && <p>Anonymous Donation</p>}
            </li>
          ))}
        </ul>
      ) : (
        <p>You haven't made any donations yet.</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    maxWidth: '600px',
    margin: 'auto',
  },
  title: {
    textAlign: 'center',
    color: '#007bff',
  },
  donationItem: {
    margin: '15px 0',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: 'inset 0 2px 5px rgba(0, 0, 0, 0.1)',
  },
};

export default DonationHistory;
